end

Scripts.OnLoad:
OFAllowLoansCheckButtonText:SetFontObject(GameFontHighlightSmall)
OFAllowLoansCheckButtonText:SetText("Allow loan")
OFAllowLoansCheckButtonText:SetScript("OnEnter", function ()
    GameTooltip:SetOwner(OFAllowLoansCheckButtonText, "ANCHOR_TOPRIGHT", 100, 15);
    GameTooltip_SetTitle(GameTooltip, "Buyout with loan");
    GameTooltip_AddNormalLine(GameTooltip, "Allow players to buy the item on a loan. They have 7 days to pay or declare bankruptcy.", true);
    GameTooltip:Show();
end)
OFAllowLoansCheckButtonText:SetScript("OnLeave", function ()
    GameTooltip:Hide();
end)

OFRoleplayCheckButtonText:SetFontObject(GameFontHighlightSmall);
OFRoleplayCheckButtonText:SetText("Roleplay");
OFRoleplayCheckButtonText:SetScript("OnEnter", function ()
    GameTooltip:SetOwner(OFRoleplayCheckButtonText, "ANCHOR_TOPRIGHT", 100, 15);
    GameTooltip_SetTitle(GameTooltip, "Roleplay");
    GameTooltip_AddNormalLine(GameTooltip, OF_ROLEPLAY_TOOLTIP, true);
    GameTooltip:Show();
end)
OFRoleplayCheckButtonText:SetScript("OnLeave", function ()
    GameTooltip:Hide();
end)

